    function withCapped(disc,cap,bg,minPrice,defaultPrice){
        var limit = cap/disc;
        var imageBackground = bg;

        if($('#special_price_box').length){
            var lastPriceDetailString = $('#special_price_box').text().replace('RM ','').replace(/\,/g, '').replace(/\./g, '').trim();
            var lastPriceInt = lastPriceString / 100;
            var lastPriceDetailInt = parseInt(lastPriceDetailString);

            if(lastPriceDetailInt < limit){
                var newPrice = Math.floor(lastPriceDetailInt - lastPriceDetailInt * disc);
                var newPriceCurr = newPrice.toString().replace(/\B(?=(\d{2})(?!\d))/g, ".");

                if(lastPriceDetailInt >= minPrice){
                    var printPrice = '<div class="mobile-block mobile-block-mc"><div class="discounted-price" style="background: url(' + imageBackground + ') no-repeat 0px center/ auto 100% #fff;">' + 'RM ' + newPriceCurr + '</div></div>';
                }

                $('#product-price-box .prod_pricebox_price').after(printPrice);

                return printPrice;
            } else if (lastPriceDetailInt >= limit){
                var newPrice = lastPriceDetailInt - cap;
                var newPriceCurr = newPrice.toString().replace(/\B(?=(\d{2})(?!\d))/g, ".");

                var printPrice = '<div class="mobile-block mobile-block-mc"><div class="discounted-price" style="background: url(' + imageBackground + ') no-repeat 0px center/ auto 100% #fff;">' + 'RM ' + newPriceCurr + '</div></div>';

                if(lastPriceDetailInt >= minPrice){
                    $('#product-price-box .prod_pricebox_price').after(printPrice);
                }

                return printPrice;
            }
        }else if($('.product-card').length || $('.unit').length){
            var lastPriceString = defaultPrice.text().replace('RM ','').replace(/\,/g, '').replace(/\./g, '').trim();
            var lastPriceInt = lastPriceString / 100;
            var lastPriceInt = parseInt(lastPriceString);

            if(lastPriceInt < limit){
                var newPrice = Math.floor(lastPriceInt - lastPriceInt * disc);
                var newPriceCurr = newPrice.toString().replace(/\B(?=(\d{2})(?!\d))/g, ".");

                var printPrice = '<div class="mobile-block-mc"><div class="discounted-price" style="background: url(' + imageBackground + ') no-repeat 0px center/ auto 100% #fff;">' + 'RM ' + newPriceCurr + '</div></div>';

                if(lastPriceInt >= minPrice){
                    //defaultPrice.after(printPrice);
                    defaultPrice.parent().parent().after(printPrice);
                }

                return printPrice;
            } else if (lastPriceInt >= limit){
                var newPrice = lastPriceInt - cap;
                var newPriceCurr = newPrice.toString().replace(/\B(?=(\d{2})(?!\d))/g, ".");

                var printPrice = '<div class="mobile-block-mc"><div class="discounted-price" style="background: url(' + imageBackground + ') no-repeat 0px center/ auto 100% #fff;">' + 'RM ' + newPriceCurr + '</div></div>';

                if(lastPriceInt >= minPrice){
                    //defaultPrice.after(printPrice);
                    defaultPrice.parent().parent().after(printPrice);
                }

                return printPrice;
            }
        }
    }

